  const [isVisible, toggle] = useToggle()

  return(
    <div className='App'>
      <button onClick={toggle}>{isVisible ? "Hide" : "Show" }</button>
        {isVisible && <h1> Hidden text </h1>}

        <button onClick={toggle}>{isVisible ? "Hide" : "Show" }</button>
        {isVisible && <h1> Hidden text </h1>}
      {isVisible && <h1> Hidden text</h1>}
    </div>
  );
}

export default App;
